import { Box, Typography } from '@mui/material';
import React, { useEffect, useState } from 'react';
import { getAllMovies } from '../../api-helpers/api-helpers';
import MovieItem from './MovieItem';

const Movies = () => {
  const [movies, setMovies] = useState([]);
  useEffect(() => {
    getAllMovies.then((data)=>setMovies(data.movies)).catch((err)=>console.log(err));
  }, []);
  return (
    <Box margin={"auto"} marginTop={4} >
      <Typography variant="h4" padding={2} width="40%" margin={"auto"} bgcolor={'#900C3F'} color={'white'} textAlign={"center"}>
        All Movies
      </Typography>
      <Box width={"100%"} margin={'auto'} display="flex" flexDirection="row" justifyContent={"center"} flexWrap={"wrap"}>
        {movies && movies.map((movie,index)=><MovieItem key={index} id={movies._id} posterUrl={movies.posterUrl} releaseDate={movies.releaseDate} />)}
      </Box>
    </Box>
  );
};

export default Movies;
