import React, { Fragment, useEffect, useState } from 'react'
import { getUserBooking } from '../api-helpers/api-helpers';
import { Box, Typography } from '@mui/material';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';

const UserProfile = () => {
  const [bookings, setBookings] = useState();
  const [error, setError] = useState(null);

  useEffect(() => {
    getUserBooking()
      .then((res) => {
        console.log('API Response:', res);
        setBookings(res.bookings);
      })
      .catch((err) => {
        console.error('Error:', err);
        setError(err.message);
      });
  }, []);

  


  console.log(bookings);
  return (
    <Box width={"100%"}  display={"flex"}>
      { bookings && bookings.length > 0 && (
      <Fragment>
       <Box width={"30%"} flexDirection={"column"} justifyContent={"center"} alignItems={"center"}>
        <AccountCircleIcon sx={{ fontSize :"10rem"}} />
        <Typography padding={1} width={'auto'} textAlign={'center'} border={'1px solid #ccc'} borderRadius={6}>
          Name: {bookings[0].user && bookings[0].user.name}
        </Typography>
      </Box>
      <Box width={"70%"}></Box>
      </Fragment>
      )}
    </Box> 
  )
};  

export default UserProfile;
