import React, { useEffect, useState } from 'react';
import { AppBar, Toolbar, Box, Autocomplete, TextField, Tabs, Tab } from '@mui/material';
import MovieIcon from '@mui/icons-material/Movie';
import { getAllMovies } from '../api-helpers/api-helpers.js';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';

const Header = () => {
    const isAdminLoggedIn = useSelector((state) => state.admin.isLoggedIn);
    const isUserLoggedIn = useSelector((state) => state.user.isLoggedIn);
    const tabs = [
        { label: 'Movies', to: '/movies' },
        ...(isUserLoggedIn || isAdminLoggedIn
            ? []
            : [
                { label: 'Admin', to: '/admin' },
                { label: 'Auth', to: '/auth' },
            ]),
    ];
    const [value, setValue] = useState(0);
    const [movies, setMovies] = useState([]);
    useEffect(() => {
        getAllMovies().then((data) => setMovies(data.movies)).catch((err) => console.log(err));
    }, []);
    return (
        <div>
            <AppBar position="sticky" sx={{ bgcolor: "#2b2d42" }}>
                <Toolbar>
                    <Box width={'20%'}>
                        <MovieIcon />
                    </Box>
                    <Box width={'30%'} margin={'auto'}>
                        <Autocomplete
                            freeSolo
                            options={movies && movies.map((option) => option.title)}
                            renderInput={(params) => <TextField sx={{ input: { color: "white " } }} variant="standard" {...params} placeholder="Search Across Multiple Movies" />}
                        />
                    </Box>
                    <Box display={'flex'}>
                        <Tabs
                            textColor="inherit"
                            indicatorColor="secondary"
                            value={value}
                            onChange={(e, val) => setValue(val)}
                        > 
                            <Tab label="Movies" LinkComponent={Link} to="/movies" />
                            {!isUserLoggedIn && !isAdminLoggedIn ? (
                                <>
                                    <Tab key="admin" label="Admin" LinkComponent={Link} to="/admin" />
                                    <Tab key="auth" label="Auth" LinkComponent={Link} to="/auth" />
                                </> 
                            ) : null}
                        </Tabs>

                    </Box>
                </Toolbar>
            </AppBar>
        </div>
    );
}

export default Header;
