import React, { Fragment, useEffect, useState } from 'react';
import { getUserBooking } from '../api-helpers/api-helpers';
import { Box, IconButton, List, ListItem, ListItemText, Typography } from '@mui/material';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';

const UserProfile = () => {
  const [bookings, setBookings] = useState();

  

  

  return (
    <Box width={"100%"} display={"flex"}>
      {bookings && bookings.length > 0 ? (
        <Fragment>
          <Box width={"30%"} display={"flex"} flexDirection={"column"} justifyContent={"center"} alignItems={"center"} padding={3}>
            <AccountCircleIcon sx={{ fontSize: "10rem", textAlign: "center", marginLeft: 3 }} />
            <Typography padding={1} width={'auto'} textAlign={'center'} border={'1px solid #ccc'} borderRadius={6}>
              Name: {bookings[0].user?.name || 'Name not available'}
            </Typography>
            <Typography mt={1} padding={1} width={'auto'} textAlign={'center'} border={'1px solid #ccc'} borderRadius={6}>
              Email: {bookings[0].user?.email || 'Email not available'}
            </Typography>
          </Box> 
          <Box width={"70%"} display={'flex'} flexDirection={'column'}>
            <Typography variant='h3' fontFamily={'verdana'} textAlign={'center'} padding={2}>
              Bookings
            </Typography>
            <Box margin={'auto'} display={'flex'} flexDirection={'column'} width="80%">
              <List>
                {bookings.map((booking, index) => (
                  <ListItem key={index} sx={{ bgcolor : "#00d386", color : "white", textAlign : "center", margin : 1 }}>
                    <ListItemText sx={{margin : 1, textAlign : 'left', width : 'auto'}}>
                      Movie: {booking.movie?.title || 'Title not available'} 
                    </ListItemText>
                    <ListItemText sx={{margin : 1, textAlign : 'left', width : 'auto'}}>
                      Seat: {booking.seatNumber} 
                    </ListItemText>
                    <ListItemText sx={{margin : 1, textAlign : 'left', width : 'auto'}}>
                      Date: {new Date(booking.date).toDateString()} 
                    </ListItemText>
                    <IconButton color="error">
                      <DeleteForeverIcon/>
                    </IconButton>
                  </ListItem>
                ))}
              </List>
            </Box>
          </Box>
        </Fragment>
      ) : (
        <Typography variant='h6' padding={3}>
          No bookings found.
        </Typography>
      )}
    </Box>
  );
};

export default UserProfile;
