import axios from 'axios';

export const getAllMovies = async () => {
    try {
        console.log('Starting request to fetch movies...');
        
        const res = await axios.get("http://localhost:5000/movie");
        
        console.log('Response received:', res);

        // Check if status code is 200
        if (res.status !== 200) {
            console.error(`Unexpected status code: ${res.status}`);
            return "No Data"; // Return or handle data as needed
        }

        // Log the response data
        console.log('Response data:', res.data);

        // Return the data
        return res.data;
    } catch (err) {
        // Log the error details
        console.error('Error occurred while fetching movies:', err);
        // Optionally rethrow the error or handle it as appropriate
        throw err; // or return a specific value or message
    }
};
