import React, { Fragment, useEffect, useState } from 'react';
import { getUserBooking, getUser } from '../api-helpers/api-helpers'; // Add getUser
import { Box, IconButton, List, ListItem, ListItemText, Typography } from '@mui/material';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';

const UserProfile = () => {
  const [bookings, setBookings] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Fetch user data
    getUser().then((res) => setUser(res.user)).catch((err) => console.log(err));

    // Fetch booking data
    getUserBooking().then((res) => {
      console.log('Bookings fetched:', res.bookings);
      setBookings(res.bookings);
    }).catch((err) => console.log(err));
  }, []);

  return (
    // ... rest of your component
    <Box width={"30%"} display={"flex"} flexDirection={"column"} justifyContent={"center"} alignItems={"center"} padding={3}>
      <AccountCircleIcon sx={{ fontSize: "10rem", textAlign: "center", marginLeft: 3 }} />
      <Typography padding={1} width={'auto'} textAlign={'center'} border={'1px solid #ccc'} borderRadius={6}>
        Name: {user?.name || 'Name not available'}
      </Typography>
      <Typography mt={1} padding={1} width={'auto'} textAlign={'center'} border={'1px solid #ccc'} borderRadius={6}>
        Email: {user?.email || 'Email not available'}
      </Typography>
    </Box>
    // ... rest of your component
  );
};

export default UserProfile;
