import { Box, Button, Typography } from '@mui/material';
import React, { useEffect, useState } from 'react';
// import { Link } from 'react-router-dom';
import MovieItem from './Movies/MovieItem';
import { Link } from 'react-router-dom';
import { getAllMovies } from '../api-helpers/api-helpers';

const HomePage = () => {
    const[movies, setMovies] = useState([]);
    useEffect(() => {
        getAllMovies().then((data)=>setMovies(data.movies)).catch((err)=>console.log(err));
    }, [])
  return ( <Box width={'100%'} height='100%' margin="auto" marginTop={2}> 
    <Box margin={'auto'} width='80%' height={'40vh'} padding={2}>
        <img src="https://static.toiimg.com/photo/92219097.jpeg" 
            alt="Brahmastra" width={'100%'} height={'100%'} style={{ objectFit: 'cover' }}/>
    </Box>
    <Box padding={5} margin="auto">
        <Typography variant="h4" textAlign={'center'}>Latest Releases</Typography> 
    </Box>
    <Box display="flex" flexDirection="row" width='80%' justifyContent={'center'} flexWrap='wrap' margin="auto">
        {[1, 2, 3, 4].map((item)=><MovieItem key={item} />)}
    </Box>
    <Box display="flex" padding={5} margin="auto">
        <Button LinkComponent={Link} to="/movies" variant="outlined" sx={{margin:"auto", color:"#2b2d42"}}>View All Movies</Button>
    </Box>
  </Box>
  );
};

export default HomePage;
