import axios from "axios";

export const getAllMovies = async () => {
  try {
    const res = await axios.get("/movie");

    if (res.status !== 200) {
      console.log("No Data");
      return; // Early exit if status is not 200
    }

    return res.data; // No need for await here; res.data is already the data

  } catch (err) {
    console.error("Error fetching movies:", err); // More descriptive error message
    return; // Optional: return an empty array or null if needed
  }
};
