import React, { Fragment, useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import { getMovieDetails } from '../../api-helpers/api-helpers';
import { Box, FormLabel, TextField, Typography, Button } from '@mui/material';

const Booking = () => {
    const [movie, setMovie] = useState();
    const [inputs, setInputs] = useState({ seatNumber: "", date: "" });
    const id = useParams().id;
    console.log(id);
    useEffect(() => {
        getMovieDetails(id).then((res) => setMovie(res.movie)).catch((err) => console.log(err));
    }, [id]);
    //console.log(movie);
    const handleChange = (e) => {
        setInputs((prevState) => ({...prevState}))
    }
    return (
        <div>
            {movie &&
                <Fragment>
                    <Typography padding={3} fontFamily={'fantasy'} variant='h4' textAlign={'center'}>
                        Book tickets of Movie : {movie.title}
                    </Typography>
                    <Box display={'flex'} justifyContent={'center'}>
                        <Box display={'flex'} justifyContent={'column'} flexDirection="column" paddingTop={3} width="50%" marginRight='auto'>
                            <img width="80%" height={"300px"} src={movie.posterUrl} alt={movie.title} />
                            <Box width={"80%"} marginTop={3} padding={2}>
                                <Typography paddingTop={2}>{movie.description}</Typography>
                                <Typography fontWeight="bold" marginTop={1}>
                                    Starrer: {movie.actors.map((actor) => " " + actor + " ")}
                                </Typography>
                                <Typography fontWeight={'bold'} marginTop={1}>
                                    Release Date : {new Date(movie.releaseDate).toDateString()}
                                </Typography>
                            </Box>
                        </Box>
                        <Box width={"50%"} paddingTop={3}>
                            <form>
                                <Box padding={5} margin="auto" display={'flex'} flexDirection={"column"}>
                                    <FormLabel>Seat Number</FormLabel>
                                    <TextField name="seatNumber" type="number" margin='normal' variant='standard' />
                                    <FormLabel>Booking Date</FormLabel>
                                    <TextField name="date" type="date" margin='normal' variant='standard' />
                                    <Button type='submit' sx={{ mt: 3 }}>Book Now</Button>
                                </Box>
                            </form>
                        </Box>
                    </Box>
                </Fragment>}
        </div>
    )
}

export default Booking
